import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HeaderComponent } from '../header/header.component';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-hero',
  standalone: true,
  imports: [CommonModule,
    RouterModule
  ],
  templateUrl: './hero.component.html',
  styleUrls: ['./hero.component.css']
})


export class HeroComponent {

}
<div class="hero-section">
    <div class="hero-content">
        <div class="description">

            <img src="../../../assets/image/share.jpg" alt="event" class="event">

            <div class="title">
                <h1> ZARAHAY</h1>
            </div>

            <div class="desc">
                <span>Zarahay, où le savoir se cultive, les idées s'épanouissent, et la communauté grandit main dans la main vers la sagesse collective.</span>
            </div>

            <div class="button">
                <p routerLink="/admin" routerLinkActive="active"> REJOIGNEZ-NOUS </p>
            </div>
        </div>
    </div>
</div>
