import { Component, ElementRef, Renderer2 } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HeaderComponent } from '../header/header.component';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-hero',
  standalone: true,
  imports: [CommonModule,
    RouterModule
  ],
  templateUrl: './hero.component.html',
  styleUrls: ['./hero.component.css']
})


export class HeroComponent {
  currentSlideIndex = 0;

  constructor(private el: ElementRef, private renderer: Renderer2) {}

  ngOnInit() {
    setInterval(() => {
      this.switchSlide();
    }, 5000)
  }

  switchSlide() {
    const slides = Array.from(this.el.nativeElement.querySelectorAll("#imageSlider img"));
    this.renderer.setStyle(slides[this.currentSlideIndex], 'display', 'none');
    this.currentSlideIndex = (this.currentSlideIndex + 1) % slides.length;
    this.renderer.setStyle(slides[this.currentSlideIndex], 'display', 'block');
  }
}
