import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { DataService } from '../../data.service';
import { receivedData } from '../receivedData.model';
import { dataToSend } from 'src/app/dataToSend.model';
import { LienService } from '../lien.service';

@Component({
  selector: 'app-brouillon',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './brouillon.component.html',
  styleUrls: ['./brouillon.component.css']
})
export class BrouillonComponent implements OnInit{

  listebrouillon : receivedData[] = [];
  donneesBrouillons = new dataToSend("","","");

  constructor(private tout : DataService ,public lien : LienService ){}

//PRISE DE L'IMAGE DU BROUILLON
// priseImageBrouillon(event : any){
//   this.donneesBrouillons.image = event.target.files[0]
//   console.log(this.donneesBrouillons.image)
// }


  //POSTER UN BROUILLON
  postDraft(element : receivedData){
    this.tout.postDraft(element)
    .subscribe (
      (reponse)=>{
        console.log(reponse);
        window.location.reload()
      }
    ,
    (err)=>{
      console.log(err);
    }
    )
  }

//SUPPRESSION DE BROUILLON
supprimmerBrouillon(element : receivedData){
  this.tout.deleteSheet(element)
  .subscribe(
    (response)=>{
      console.log("suppression reussie")
      element.visible = false;
    },
    (err)=>{
      console.log("erreur lors de la suppresion")
    }
  )

}

ngOnInit() : void{
//AFFICHER LES ELEMENTS DU BROUILLON 
this.tout.list()
.subscribe(
  (response)=>{

    for(let i = 0 ; i < response.length ; i++){
      this.listebrouillon[i] = new receivedData( response[i].id_brouillon ,response[i].nom , response[i].description , response[i].nom_image , response[i].image_path ,response[i].date)
     console.log(response[i].nom + "\n" + response[i].description)
    } 

    for(let i = 0  ; i < this.listebrouillon.length ; i++){
       this.listebrouillon[i].absolute_path = "http://localhost:3000/" + this.listebrouillon[i].path_img;
    }

  },

  (erreur) => {
    console.log("erreur d'affichage")
  }
)
}
}
<div class="sheetEvents">
    <h1>Liste Des Brouillons</h1>
<div class="historique_container">
    <div *ngFor="let element of listebrouillon"  >
        <div *ngIf="element.visible" class="draft">
            <div   class="historiques" [ngStyle]="{'background-image': 'url(' + element.absolute_path+ ')'}">
                <h2>{{element.nom}}</h2>
                <p> description : </p> <br>
                <p>{{element.description}}</p>
                <p>date de fin : {{element.date.toLocaleDateString('en-GB')}}</p>
            </div>
            <div class="mini-menu">
                <img src="../../../../assets/image/supprimer.png" alt="" (click)="supprimmerBrouillon(element)">
                <img src="../../../../assets/image/poster3.png" alt="" (click)="postDraft(element)">
                <img src="../../../../assets/image/edit4.png" alt="" (click)="lien.updateDraft(element)">
            </div>
        </div>

        <div *ngIf="!element.visible" class="historiques" [ngStyle]="{'background-image': 'url(' + element.absolute_path+ ')'}">
            <h2>{{element.nom}}</h2>
            <p> description : </p> <br>
            <p>{{element.description}}</p>
            <p>date de fin : {{element.date.toLocaleDateString('en-GB')}}</p>
            <button (click)="supprimmerBrouillon(element)">supprimer</button>
            <button (click)="lien.updateDraft(element)">modifier</button>
        </div>
    </div>
</div>
</div>
