import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { HeaderComponent } from '../header/header.component';
import { RouterModule } from '@angular/router';

@Component({
  selector: 'app-hero',
  standalone: true,
  imports: [CommonModule,
    RouterModule
  ],
  templateUrl: './hero.component.html',
  styleUrls: ['./hero.component.css']
})


export class HeroComponent {

  slideIndex = 1;
  slides = [{ image: "../../../assets/image/gorbache.jpg", caption: "description de l'image 1" }, { image: '../../../assets/image/asian.jpg', caption: "description de l'image 2" }, { image: '../../../assets/image/gorbache.jpg', caption: "description de l'image 3" }];

  dots = Array(this.slides.length).fill(0);

  constructor() { }

  plusSlides(n: number): void {
    this.showSlides(this.slideIndex += n);
  }

  currentSlide(n: number): void {
    this.showSlides(this.slideIndex = n);
  }

  showSlides(n: number): void {
    if (n > this.slides.length) {
      this.slideIndex = 1;
    }
    if (n < 1) {
      this.slideIndex = this.slides.length;
    }
    // Update the DOM here
  }
}
